package com.example.suresales;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class AllCarActivity extends AppCompatActivity {

    private RecyclerView carsRecView;
    private CarRecViewAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_all_car);

        adapter = new CarRecViewAdapter(this);
        carsRecView = findViewById(R.id.carsRecView);

        carsRecView.setAdapter(adapter);
        carsRecView.setLayoutManager(new GridLayoutManager(this, 2));

        ArrayList<Car> cars = new ArrayList<>();
        cars.add(new Car(1, "toyota", 1245, 0.1, "Black", 1234, true,4.40, "data:image/jpeg;base64,"));
        adapter.setCars(cars);
    }
}